const fs = require('fs');
const path = '/home/paul/Projects/wf-monorepo/.agentTask/discussion.md';

// Read current discussion file
let currentContent = '';
try {
  currentContent = fs.readFileSync(path, 'utf8');
} catch (error) {
  throw new Error('No active discussion found. Create a discussion first.');
}

// Check if discussion is already resolved
if (currentContent.includes('[x]') || currentContent.includes('[X]')) {
  throw new Error('Discussion is already resolved. Create a new discussion instead.');
}

const body = $input.item.json.body || $input.item.json;
const { comment, agent = 'unknown' } = body;

if (!comment) {
  throw new Error('comment is required');
}

const timestamp = new Date().toISOString();

return {
  json: {
    currentContent,
    comment,
    agent,
    timestamp
  }
};

const fs = require('fs');
const data = $input.item.json;

const newComment = `\n**${data.agent}** - ${data.timestamp}\n> ${data.comment}\n`;

// Find the Resolution section and insert before it
const resolutionIndex = data.currentContent.indexOf('## Resolution');

let updatedContent;
if (resolutionIndex !== -1) {
  updatedContent = 
    data.currentContent.substring(0, resolutionIndex) +
    newComment +
    '\n---\n\n' +
    data.currentContent.substring(resolutionIndex);
} else {
  // If no Resolution section found, append to end
  updatedContent = data.currentContent + '\n---\n' + newComment;
}

const discussionPath = '/home/paul/Projects/wf-monorepo/.agentTask/discussion.md';

try {
  fs.writeFileSync(discussionPath, updatedContent, 'utf8');
  return {
    json: {
      success: true,
      message: 'Comment added to discussion',
      agent: data.agent,
      timestamp: data.timestamp
    }
  };
} catch (error) {
  throw new Error(`Failed to update discussion file: ${error.message}`);
}
