// Get all query items from the database
const queries = $input.all();

// Log for debugging
console.log('Input queries:', queries.length);

// Return each query as a separate item for processing
const result = [];
for (let i = 0; i < queries.length; i++) {
  const item = queries[i];
  result.push({
    json: {
      query: item.json.qrySQL,
      queryName: item.json.qryName,
      index: i + 1,
      agent: 'session-startup',
      params: {}
    }
  });
}

console.log('Returning items:', result.length);
return result;

// Handle the HTTP response data safely
const results = $input.all();
console.log('Format Output received:', results.length, 'items');

// Extract query results, handling errors gracefully
const queryResults = results.map((item, index) => {
  console.log(`Item ${index}:`, Object.keys(item.json || {}));

  if (item.json && item.json.results && item.json.results.data) {
    return {
      queryName: `query${index + 1}`,
      success: true,
      data: item.json.results.data
    };
  } else {
    return {
      queryName: `query${index + 1}`,
      success: false,
      error: item.json ? (item.json.error || 'No results') : 'No response',
      data: []
    };
  }
});

return [{ 
  json: { 
    session_context: {
      queries: queryResults,
      timestamp: new Date().toISOString(),
      total_queries: queryResults.length,
      successful_queries: queryResults.filter(q => q.success).length
    }
  } 
}];
