const body = $input.item.json.body || $input.item.json;
const { 
  plan_id, 
  from_agent = 'claude',
  to_agent = 'any',
  type,
  subject,
  message,
  created_by = 'claude'
} = body;

if (!plan_id) {
  throw new Error('plan_id is required');
}

if (!type) {
  throw new Error('type is required');
}

if (!subject) {
  throw new Error('subject is required');
}

if (!message) {
  throw new Error('message is required');
}

const validTypes = ['guidance', 'question', 'response', 'decision', 'status', 'analysis', 'technique'];
if (!validTypes.includes(type)) {
  throw new Error(`Invalid type. Must be one of: ${validTypes.join(', ')}`);
}

return {
  json: {
    plan_id,
    from_agent,
    to_agent,
    type,
    subject,
    message,
    created_by
  }
};

const comm = $('Validate Communication').item.json;
const insertResult = $input.item.json;

return [{
  json: {
    success: true,
    comm_id: insertResult.insertId || insertResult.id,
    communication: {
      plan_id: comm.plan_id,
      from_agent: comm.from_agent,
      to_agent: comm.to_agent,
      type: comm.type,
      subject: comm.subject
    },
    timestamp: comm.timestamp
  }
}];
