// Transform raw column metadata into grid columns
const columns = [];

for (const col of $input.all()) {
  const field = col.json;

  // Generate label from name (camelCase -> Title Case)
  const label = field.name
    .replace(/([A-Z])/g, ' $1')
    .replace(/^./, str => str.toUpperCase())
    .trim();

  // Determine width based on dataType
  let width = 150;
  if (field.dataType === 'int' || field.dataType === 'bigint') {
    width = 80;
  } else if (field.dataType === 'text' || field.dataType === 'longtext') {
    width = 250;
  } else if (field.dataType === 'date' || field.dataType === 'datetime') {
    width = 120;
  } else if (field.maxLength && field.maxLength < 50) {
    width = 100;
  }

  // Determine alignment
  let align = 'left';
  if (field.dataType === 'int' || field.dataType === 'bigint' || field.dataType === 'decimal' || field.dataType === 'float') {
    align = 'right';
  } else if (field.dataType === 'date' || field.dataType === 'datetime') {
    align = 'center';
  }

  // Build column config
  const column = {
    name: field.name,
    label: label,
    field: field.name,
    width: width,
    align: align,
    sortable: true,
    filterable: field.columnKey !== 'PRI'
  };

  // Add formatter hints for special types
  if (field.dataType === 'date') {
    column.format = 'date';
  } else if (field.dataType === 'datetime' || field.dataType === 'timestamp') {
    column.format = 'datetime';
  } else if (field.name.includes('email')) {
    column.format = 'email';
  } else if (field.name.includes('url') || field.name.includes('link')) {
    column.format = 'url';
  }

  columns.push(column);
}

return [{ json: { columns } }];
